            );
        } else {
            return widgetContext.rxjs.of(null);
        }
    }

    function loadSensor() {
        widgetContext.rxjs.forkJoin([
            attributeService.getEntityAttributes(entityId, 'SERVER_SCOPE', ['latitude', 'longitude']),
            deviceService.getDevice(entityId.id)
        ]).subscribe(
            function (data) {
                var attributes = data[0];
                for (var i = 0; i < attributes.length; i ++) {
                    if (attributes[i].key === 'latitude' || attributes[i].key === 'longitude') {
                        if (!vm.location) {
                            vm.location = [];
                        }
                        vm.location[attributes[i].key === 'latitude' ? 0 : 1] = attributes[i].value;
                    }
                }
                vm.sensor = data[1];
                vm.editSensorFormGroup.get('name').patchValue(vm.sensor.name, {emitEvent: false});
                vm.editSensorFormGroup.get('label').patchValue(vm.sensor.label, {emitEvent: false});
                createLocationSubscription();
            }
        );
    }

}

